// Página 63 - Código 12. Classe TestaOperadores.

public class TestaOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int valor1=10;
		int valor2=5; 
		
		if(valor1==valor2) System.out.println("valor1 e igual a valor2"); 
		
		if(valor1!=valor2) System.out.println("valor1 nao e igual a valor2"); 
		
		if(valor1>valor2) System.out.println("valor1 e maior do que valor2"); 
		
		if(valor1<valor2) System.out.println("valor1 e menor do que valor2"); 
		
		if(valor1>=valor2) System.out.println("valor1 e maior ou igual a valor2"); 
		
		if(valor1<=valor2) System.out.println("valor1 e menor ou igual a valor2");
	}
}
